import ScanResults from "../ScanResults";

export default function ScanResultsExample() {
  const mockRecords = [
    {
      type: "SPF",
      status: "PASS" as const,
      record: "v=spf1 include:_spf.google.com ~all",
    },
    {
      type: "DKIM",
      status: "WARN" as const,
      record: "v=DKIM1; k=rsa; p=MIGfMA0GCSqGSIb3DQEBAQUAA4...",
      issues: ["Weak key size (1024-bit)"],
      suggestions: ["Upgrade to 2048-bit RSA key"],
    },
    {
      type: "DMARC",
      status: "FAIL" as const,
      issues: ["No DMARC record found"],
      suggestions: ["Add DMARC record: v=DMARC1; p=quarantine; rua=mailto:dmarc@example.com"],
    },
    {
      type: "BIMI",
      status: "FAIL" as const,
      issues: ["BIMI not configured"],
      suggestions: ["Optional: Add BIMI record for brand logo in inbox"],
    },
    {
      type: "MX",
      status: "PASS" as const,
      record: "10 mx1.example.com, 20 mx2.example.com",
    },
  ];

  return (
    <ScanResults
      domain="example.com"
      overallStatus="WARN"
      criticalIssues={1}
      records={mockRecords}
      onGenerateReport={() => console.log("Generate report triggered")}
      onShare={() => console.log("Share triggered")}
    />
  );
}

client/src/components/DomainCard.tsx.content:
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { ExternalLink, RefreshCw } from "lucide-react";
import StatusBadge from "./StatusBadge";

interface DomainCardProps {
  domain: string;
  lastScanDate: string;
  status: "PASS" | "WARN" | "FAIL";
  criticalIssues: number;
  onRescan?: () => void;
  onViewDetails?: () => void;
}

export default function DomainCard({
  domain,
  lastScanDate,
  status,
  criticalIssues,
  onRescan,
  onViewDetails,
}: DomainCardProps) {
  return (
    <Card className="p-6" data-testid={`card-domain-${domain}`}>
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <h3 className="text-xl font-semibold mb-2" data-testid={`text-domain-name-${domain}`}>
            {domain}
          </h3>
          <p className="text-sm text-muted-foreground mb-3">
            Last scan: {lastScanDate}
          </p>
          <StatusBadge status={status} />
        </div>
      </div>

      {criticalIssues > 0 && (
        <div className="mb-4 p-3 bg-destructive/10 rounded-md">
          <p className="text-sm text-destructive font-medium">
            {criticalIssues} critical {criticalIssues === 1 ? "issue" : "issues"} found
          </p>
        </div>
      )}

      <div className="flex gap-2">
        <Button
          variant="outline"
          className="flex-1"
          onClick={onRescan}
          data-testid={`button-rescan-${domain}`}
        >
          <RefreshCw className="h-4 w-4 mr-2" />
          Rescan
        </Button>
        <Button
          variant="default"
          className="flex-1"
          onClick={onViewDetails}
          data-testid={`button-view-details-${domain}`}
        >
          <ExternalLink className="h-4 w-4 mr-2" />
          View Details
        </Button>
      </div>
    </Card>
  );
}

