import { Router } from "express";
import { storage } from "../storage";
import { requireAuth } from "../auth-routes";
import { requireDomainAccess } from "../middleware/accessControl";
import { requireFeature } from "../middleware/planLimits";
import { logAuditEvent, AuditEvents } from "../services/auditLog";
import { randomBytes } from "crypto";

const router = Router();

// Create public report link
router.post("/domains/:id/public-report", 
  requireAuth,
  requireFeature("shareLinks"),
  requireDomainAccess(),
  async (req, res) => {
    try {
      const user = req.user as any;
      const { id: domainId } = req.params;
      const { runId, expiresInDays } = req.body;

      // Validate run belongs to domain
      const run = await storage.getScanRunsByDomainId(domainId, 1);
      if (!run || run.length === 0 || run[0].id !== runId) {
        return res.status(404).json({ error: "Scan run not found for this domain" });
      }

      // Generate unique token
      const token = randomBytes(16).toString("hex");

      // Calculate expiry if specified
      let expiresAt = null;
      if (expiresInDays && expiresInDays > 0) {
        expiresAt = new Date();
        expiresAt.setDate(expiresAt.getDate() + expiresInDays);
      }

      const publicReport = await storage.createPublicReport({
        domainId,
        runId,
        token,
        expiresAt,
      });

      // Log audit event
      await logAuditEvent({
        actorUserId: user.id,
        event: AuditEvents.PUBLIC_LINK_CREATED,
        domainId,
        meta: {
          publicReportId: publicReport.id,
          runId,
          expiresAt: expiresAt?.toISOString(),
        },
      });

      res.json({
        ...publicReport,
        url: `/r/${token}`,
      });
    } catch (error: any) {
      res.status(400).json({ error: error.message });
    }
  }
);

// Get all public reports for a domain
router.get("/domains/:id/public-reports",
  requireAuth,
  requireDomainAccess(),
  async (req, res) => {
    const { id: domainId } = req.params;
    const publicReports = await storage.getPublicReportsByDomainId(domainId);
    res.json(publicReports);
  }
);

// Revoke public report
router.delete("/public-reports/:id",
  requireAuth,
  async (req, res) => {
    try {
      const user = req.user as any;
      const { id } = req.params;

      // Get public report to check ownership
      const publicReports = await storage.getPublicReportsByDomainId("");
      const publicReport = publicReports.find(r => r.id === id);

      if (!publicReport) {
        return res.status(404).json({ error: "Public report not found" });
      }

      // Check domain access
      const domain = await storage.getDomain(publicReport.domainId);
      if (!domain || domain.userId !== user.id) {
        return res.status(403).json({ error: "Access denied" });
      }

      await storage.deletePublicReport(id);

      // Log audit event
      await logAuditEvent({
        actorUserId: user.id,
        event: AuditEvents.PUBLIC_LINK_REVOKED,
        domainId: publicReport.domainId,
        meta: {
          publicReportId: id,
        },
      });

      res.json({ success: true });
    } catch (error: any) {
      res.status(400).json({ error: error.message });
    }
  }
);

// Public route to view report (no auth required)
router.get("/r/:token", async (req, res) => {
  try {
    const { token } = req.params;

    const publicReport = await storage.getPublicReportByToken(token);

    if (!publicReport) {
      return res.status(404).json({ error: "Report not found" });
    }

    // Check expiry
    if (publicReport.expiresAt && new Date(publicReport.expiresAt) < new Date()) {
      return res.status(404).json({ error: "Report link has expired" });
    }

    // Get scan run data
    const runs = await storage.getScanRunsByDomainId(publicReport.domainId);
    const run = runs.find(r => r.id === publicReport.runId);

    if (!run) {
      return res.status(404).json({ error: "Scan run not found" });
    }

    // Get scan records
    const records = await storage.getScanRecordsByRunId(run.id);

    // Get domain name
    const domain = await storage.getDomain(publicReport.domainId);

    res.json({
      domain: domain?.name || "Unknown",
      run: {
        id: run.id,
        status: run.status,
        score: run.score,
        scoreBreakdown: run.scoreBreakdown,
        startedAt: run.startedAt,
        finishedAt: run.finishedAt,
      },
      records: records.map(r => ({
        type: r.recordType,
        selector: r.selector,
        verdict: r.verdict,
        value: r.rawValue,
        meta: r.metaJson,
      })),
    });
  } catch (error: any) {
    res.status(500).json({ error: error.message });
  }
});

export default router;

server/routes/admin.ts.content:
import { Router } from "express";
import { storage } from "../storage";
import { requireAuth } from "../auth-routes";
import { db } from "../db";
import { users, domains, scanRuns, alerts, emailLog, appEvents } from "@shared/schema";
import { eq, and, gte, sql } from "drizzle-orm";
import { AppEventType } from "../services/analytics";

const router = Router();

// Middleware to check if user is admin (for now, just check if they're authenticated)
// In a real app, you'd have an isAdmin field on the user
function requireAdmin(req: any, res: any, next: any) {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ error: "Not authenticated" });
  }

  // For now, all authenticated users can see metrics
  // In production, you'd check: if (!req.user.isAdmin) { return res.status(403)... }

  next();
}

// Get admin metrics
router.get("/metrics", requireAuth, requireAdmin, async (req, res) => {
  try {
    // Calculate date ranges
    const now = new Date();
    const last24h = new Date(now.getTime() - 24 * 60 * 60 * 1000);
    const last7d = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);

    // Active users by plan
    const allUsers = await db.select().from(users);
    const usersByPlan = allUsers.reduce((acc, user) => {
      const plan = user.plan || "Free";
      acc[plan] = (acc[plan] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    // Total domains monitored
    const allDomains = await db.select().from(domains);
    const totalDomains = allDomains.length;
    const monitoredDomains = allDomains.filter(d => d.monitoringEnabled === "true").length;

    // Scans in last 24h and 7d
    const scansLast24h = await db
      .select({ count: sql<number>`count(*)` })
      .from(scanRuns)
      .where(gte(scanRuns.startedAt, last24h));

    const scansLast7d = await db
      .select({ count: sql<number>`count(*)` })
      .from(scanRuns)
      .where(gte(scanRuns.startedAt, last7d));

    // Alerts in last 24h
    const alertsLast24h = await db
      .select({ count: sql<number>`count(*)` })
      .from(alerts)
      .where(gte(alerts.sentAt, last24h));

    // Email failures (you'd track this with a status field in email_log in a real app)
    const emailFailures = await db
      .select({ count: sql<number>`count(*)` })
      .from(emailLog)
      .where(
        and(
          gte(emailLog.sentAt, last24h),
          eq(emailLog.type, "error")
        )
      );

    res.json({
      users: {
        byPlan: usersByPlan,
        total: allUsers.length,
      },
      domains: {
        total: totalDomains,
        monitored: monitoredDomains,
      },
      scans: {
        last24h: Number(scansLast24h[0]?.count || 0),
        last7d: Number(scansLast7d[0]?.count || 0),
      },
      alerts: {
        last24h: Number(alertsLast24h[0]?.count || 0),
      },
      email: {
        failuresLast24h: Number(emailFailures[0]?.count || 0),
      },
    });
  } catch (error: any) {
    res.status(500).json({ error: error.message });
  }
});

// Get events analytics
router.get("/metrics/events", requireAuth, requireAdmin, async (req, res) => {
  try {
    const { days = "30" } = req.query;
    const daysNum = parseInt(days as string);
    const since = new Date(Date.now() - daysNum * 24 * 60 * 60 * 1000);

    // Get all events since date
    const events = await db
      .select()
      .from(appEvents)
      .where(gte(appEvents.createdAt, since))
      .orderBy(appEvents.createdAt);

    // Get ALL possible event types from enum
    const eventTypes = Object.values(AppEventType);

    // Initialize event counts with zeros for all event types
    const eventCounts: Record<string, number> = {};
    eventTypes.forEach(eventType => {
      eventCounts[eventType] = 0;
    });

    // Fill in actual event counts
    events.forEach(event => {
      if (eventCounts.hasOwnProperty(event.event)) {
        eventCounts[event.event]++;
      }
    });

    // Generate full date range
    const dateRange: string[] = [];
    for (let i = daysNum - 1; i >= 0; i--) {
      const date = new Date(Date.now() - i * 24 * 60 * 60 * 1000);
      dateRange.push(date.toISOString().split('T')[0]);
    }

    // Initialize timeline with all dates and zero counts for all event types
    const timelineMap: Record<string, Record<string, number>> = {};
    dateRange.forEach(date => {
      timelineMap[date] = {};
      eventTypes.forEach(eventType => {
        timelineMap[date][eventType] = 0;
      });
    });

    // Fill in actual event counts
    events.forEach(event => {
      const date = new Date(event.createdAt).toISOString().split('T')[0];
      if (timelineMap[date]) {
        timelineMap[date][event.event] = (timelineMap[date][event.event] || 0) + 1;
      }
    });

    // Format timeline data
    const timeline = dateRange.map(date => ({
      date,
      ...timelineMap[date],
    }));

    res.json({
      total: events.length,
      byType: eventCounts,
      timeline,
    });
  } catch (error: any) {
    res.status(500).json({ error: error.message });
  }
});

export default router;

