          )}
          {hasTeamsFeature && (
            <TabsTrigger value="teams" data-testid="tab-teams">
              Teams
            </TabsTrigger>
          )}
        </TabsList>

        <TabsContent value="alert-prefs">
          <AlertPrefsTab />
        </TabsContent>

        <TabsContent value="billing">
          <BillingTab />
        </TabsContent>

        {(hasSlackFeature || hasWebhookFeature) && (
          <TabsContent value="destinations">
            <DestinationsTab />
          </TabsContent>
        )}

        {hasTeamsFeature && (
          <TabsContent value="teams">
            <TeamsTab />
          </TabsContent>
        )}
      </Tabs>
    </div>
  );
}

