import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Plus, Users, Crown, Shield, Eye, Trash2, UserPlus } from "lucide-react";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface Team {
  id: string;
  name: string;
  ownerUserId: string;
  brandLogo?: string | null;
  brandColor?: string | null;
  createdAt: string;
  role?: string;
}

interface TeamMember {
  id: string;
  teamId: string;
  userId: string;
  role: string;
  email?: string;
  createdAt: string;
}

export default function TeamsTab() {
  const { toast } = useToast();
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [showInviteDialog, setShowInviteDialog] = useState(false);
  const [selectedTeam, setSelectedTeam] = useState<Team | null>(null);
  const [newTeamName, setNewTeamName] = useState("");
  const [brandLogo, setBrandLogo] = useState("");
  const [brandColor, setBrandColor] = useState("");
  const [inviteEmail, setInviteEmail] = useState("");
  const [inviteRole, setInviteRole] = useState<"admin" | "viewer">("viewer");

  const { data: teamsData, isLoading } = useQuery<{ owned: Team[]; member: Team[] }>({
    queryKey: ["/api/teams"],
  });

  const { data: membersData } = useQuery<TeamMember[]>({
    queryKey: ["/api/teams", selectedTeam?.id, "members"],
    enabled: !!selectedTeam,
    queryFn: () =>
      fetch(`/api/teams/${selectedTeam?.id}/members`).then((r) => r.json()),
  });

  const createTeamMutation = useMutation({
    mutationFn: async (data: { name: string; brandLogo?: string; brandColor?: string }) => {
      const res = await apiRequest("POST", "/api/teams", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teams"] });
      setShowCreateDialog(false);
      setNewTeamName("");
      setBrandLogo("");
      setBrandColor("");
      toast({
        title: "Team Created",
        description: "Your new team has been created successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Create Team",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const inviteMemberMutation = useMutation({
    mutationFn: async (data: { teamId: string; email: string; role: string }) => {
      const res = await apiRequest("POST", `/api/teams/${data.teamId}/members`, {
        email: data.email,
        role: data.role,
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teams", selectedTeam?.id, "members"] });
      setShowInviteDialog(false);
      setInviteEmail("");
      setInviteRole("viewer");
      toast({
        title: "Member Invited",
        description: "Team member has been added successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Invite Member",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const removeMemberMutation = useMutation({
    mutationFn: async (data: { teamId: string; userId: string }) => {
      const res = await apiRequest("DELETE", `/api/teams/${data.teamId}/members/${data.userId}`);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teams", selectedTeam?.id, "members"] });
      toast({
        title: "Member Removed",
        description: "Team member has been removed successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Remove Member",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const updateTeamMutation = useMutation({
    mutationFn: async (data: { teamId: string; brandLogo?: string; brandColor?: string }) => {
      const res = await apiRequest("PATCH", `/api/teams/${data.teamId}`, {
        brandLogo: data.brandLogo,
        brandColor: data.brandColor,
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teams"] });
      toast({
        title: "Team Updated",
        description: "Team branding has been updated successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Update Team",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleCreateTeam = () => {
    if (!newTeamName.trim()) {
      toast({
        title: "Team Name Required",
        description: "Please enter a team name.",
        variant: "destructive",
      });
      return;
    }

    createTeamMutation.mutate({
      name: newTeamName.trim(),
      brandLogo: brandLogo || undefined,
      brandColor: brandColor || undefined,
    });
  };

  const handleInviteMember = () => {
    if (!selectedTeam || !inviteEmail.trim()) return;

    inviteMemberMutation.mutate({
      teamId: selectedTeam.id,
      email: inviteEmail.trim(),
      role: inviteRole,
    });
  };

  const handleRemoveMember = (userId: string) => {
    if (!selectedTeam) return;

    removeMemberMutation.mutate({
      teamId: selectedTeam.id,
      userId,
    });
  };

  const getRoleIcon = (role: string) => {
    switch (role) {
      case "owner":
        return <Crown className="h-4 w-4" />;
      case "admin":
        return <Shield className="h-4 w-4" />;
      case "viewer":
        return <Eye className="h-4 w-4" />;
      default:
        return null;
    }
  };

  const getRoleBadgeVariant = (role: string): "default" | "secondary" | "outline" => {
    switch (role) {
      case "owner":
        return "default";
      case "admin":
        return "secondary";
      default:
        return "outline";
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <p className="text-muted-foreground">Loading teams...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>My Teams</CardTitle>
              <CardDescription>Manage your teams and collaboration</CardDescription>
            </div>
            <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
              <DialogTrigger asChild>
                <Button data-testid="button-create-team">
                  <Plus className="h-4 w-4 mr-2" />
                  Create Team
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Create New Team</DialogTitle>
                  <DialogDescription>
                    Create a team to collaborate with others on domain monitoring
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4 py-4">
                  <div>
                    <Label htmlFor="team-name">Team Name</Label>
                    <Input
                      id="team-name"
                      value={newTeamName}
                      onChange={(e) => setNewTeamName(e.target.value)}
                      placeholder="Acme Corp Team"
                      data-testid="input-team-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="brand-logo">Brand Logo URL (optional)</Label>
                    <Input
                      id="brand-logo"
                      value={brandLogo}
                      onChange={(e) => setBrandLogo(e.target.value)}
                      placeholder="https://example.com/logo.png"
                      data-testid="input-brand-logo"
                    />
                  </div>
                  <div>
                    <Label htmlFor="brand-color">Brand Color (optional)</Label>
                    <Input
                      id="brand-color"
                      value={brandColor}
                      onChange={(e) => setBrandColor(e.target.value)}
                      placeholder="#0066FF"
                      data-testid="input-brand-color"
                    />
                  </div>
                </div>
                <div className="flex justify-end gap-2">
                  <Button
                    variant="outline"
                    onClick={() => setShowCreateDialog(false)}
                    data-testid="button-cancel-create"
                  >
                    Cancel
                  </Button>
                  <Button
                    onClick={handleCreateTeam}
                    disabled={createTeamMutation.isPending}
                    data-testid="button-confirm-create"
                  >
                    {createTeamMutation.isPending ? "Creating..." : "Create Team"}
                  </Button>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {teamsData?.owned && teamsData.owned.length > 0 ? (
              <>
                <h3 className="text-sm font-medium">Teams You Own</h3>
                <div className="grid gap-4">
                  {teamsData.owned.map((team) => (
                    <Card key={team.id} className="p-4" data-testid={`card-team-${team.id}`}>
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                          <Users className="h-5 w-5 text-muted-foreground" />
                          <div>
                            <p className="font-medium" data-testid={`text-team-name-${team.id}`}>
                              {team.name}
                            </p>
                            <p className="text-xs text-muted-foreground">
                              Created {new Date(team.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          <Badge variant="default">
                            <Crown className="h-3 w-3 mr-1" />
                            Owner
                          </Badge>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => setSelectedTeam(team)}
                            data-testid={`button-manage-team-${team.id}`}
                          >
                            Manage
                          </Button>
                        </div>
                      </div>
                    </Card>
                  ))}
                </div>
              </>
            ) : null}

            {teamsData?.member && teamsData.member.length > 0 ? (
              <>
                <h3 className="text-sm font-medium mt-6">Teams You're In</h3>
                <div className="grid gap-4">
                  {teamsData.member.map((team) => (
                    <Card key={team.id} className="p-4" data-testid={`card-member-team-${team.id}`}>
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                          <Users className="h-5 w-5 text-muted-foreground" />
                          <div>
                            <p className="font-medium">{team.name}</p>
                            <p className="text-xs text-muted-foreground">
                              Joined {new Date(team.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                        </div>
                        <Badge variant={getRoleBadgeVariant(team.role || "viewer")}>
                          {getRoleIcon(team.role || "viewer")}
                          <span className="ml-1 capitalize">{team.role}</span>
                        </Badge>
                      </div>
                    </Card>
                  ))}
                </div>
              </>
            ) : null}

            {(!teamsData?.owned || teamsData.owned.length === 0) &&
              (!teamsData?.member || teamsData.member.length === 0) && (
                <div className="text-center py-8">
                  <Users className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                  <p className="text-muted-foreground">No teams yet</p>
                  <p className="text-sm text-muted-foreground mb-4">
                    Create a team to collaborate with others
                  </p>
                </div>
              )}
          </div>
        </CardContent>
      </Card>

      {selectedTeam && (
        <Dialog open={!!selectedTeam} onOpenChange={() => setSelectedTeam(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Manage {selectedTeam.name}</DialogTitle>
              <DialogDescription>Manage team members and branding</DialogDescription>
            </DialogHeader>

            <div className="space-y-6 py-4">
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-sm font-medium">Team Members</h3>
                  <Dialog open={showInviteDialog} onOpenChange={setShowInviteDialog}>
                    <DialogTrigger asChild>
                      <Button size="sm" data-testid="button-invite-member">
                        <UserPlus className="h-4 w-4 mr-2" />
                        Invite Member
                      </Button>
                    </DialogTrigger>
                    <DialogContent>
                      <DialogHeader>
                        <DialogTitle>Invite Team Member</DialogTitle>
                        <DialogDescription>Add a new member to your team</DialogDescription>
                      </DialogHeader>
                      <div className="space-y-4 py-4">
                        <div>
                          <Label htmlFor="invite-email">Email Address</Label>
                          <Input
                            id="invite-email"
                            type="email"
                            value={inviteEmail}
                            onChange={(e) => setInviteEmail(e.target.value)}
                            placeholder="user@example.com"
                            data-testid="input-invite-email"
                          />
                        </div>
                        <div>
                          <Label htmlFor="invite-role">Role</Label>
                          <Select
                            value={inviteRole}
                            onValueChange={(value) => setInviteRole(value as "admin" | "viewer")}
                          >
                            <SelectTrigger data-testid="select-invite-role">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="admin">Admin - Can manage team and members</SelectItem>
                              <SelectItem value="viewer">Viewer - Read-only access</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                      </div>
                      <div className="flex justify-end gap-2">
                        <Button
                          variant="outline"
                          onClick={() => setShowInviteDialog(false)}
                          data-testid="button-cancel-invite"
                        >
                          Cancel
                        </Button>
                        <Button
                          onClick={handleInviteMember}
                          disabled={inviteMemberMutation.isPending}
                          data-testid="button-confirm-invite"
                        >
                          {inviteMemberMutation.isPending ? "Inviting..." : "Invite"}
                        </Button>
                      </div>
                    </DialogContent>
                  </Dialog>
                </div>

                <div className="space-y-2">
                  {membersData?.map((member) => (
                    <div
                      key={member.id}
                      className="flex items-center justify-between p-3 rounded-md bg-muted"
                      data-testid={`row-member-${member.id}`}
                    >
                      <div>
                        <p className="text-sm font-medium">{member.email}</p>
                        <p className="text-xs text-muted-foreground">
                          Joined {new Date(member.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                      <div className="flex items-center gap-2">
                        <Badge variant={getRoleBadgeVariant(member.role)}>
                          {getRoleIcon(member.role)}
                          <span className="ml-1 capitalize">{member.role}</span>
                        </Badge>
                        {member.role !== "owner" && (
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleRemoveMember(member.userId)}
                            disabled={removeMemberMutation.isPending}
                            data-testid={`button-remove-member-${member.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div>
                <h3 className="text-sm font-medium mb-4">Team Branding</h3>
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="edit-brand-logo">Brand Logo URL</Label>
                    <Input
                      id="edit-brand-logo"
                      value={selectedTeam.brandLogo || ""}
                      onChange={(e) =>
                        setSelectedTeam({ ...selectedTeam, brandLogo: e.target.value })
                      }
                      placeholder="https://example.com/logo.png"
                      data-testid="input-edit-brand-logo"
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-brand-color">Brand Color</Label>
                    <Input
                      id="edit-brand-color"
                      value={selectedTeam.brandColor || ""}
                      onChange={(e) =>
                        setSelectedTeam({ ...selectedTeam, brandColor: e.target.value })
                      }
                      placeholder="#0066FF"
                      data-testid="input-edit-brand-color"
                    />
                  </div>
                  <Button
                    onClick={() =>
                      updateTeamMutation.mutate({
                        teamId: selectedTeam.id,
                        brandLogo: selectedTeam.brandLogo || undefined,
                        brandColor: selectedTeam.brandColor || undefined,
                      })
                    }
                    disabled={updateTeamMutation.isPending}
                    data-testid="button-update-branding"
                  >
                    {updateTeamMutation.isPending ? "Updating..." : "Update Branding"}
                  </Button>
                </div>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

client/src/pages/settings/alert-prefs-ta.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Bell } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface AlertPref {
  userId: string;
  emailEnabled: boolean;
  slackEnabled: boolean;
  webhookEnabled: boolean;
  threshold: "info" | "warn" | "fail";
}

export default function AlertPrefsTab() {
  const { toast } = useToast();

  const { data: alertPrefs, isLoading } = useQuery<AlertPref>({
    queryKey: ["/api/alert-prefs"],
  });

  const updatePrefsMutation = useMutation({
    mutationFn: async (data: Partial<AlertPref>) => {
      const res = await apiRequest("POST", "/api/alert-prefs", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/alert-prefs"] });
      toast({
        title: "Preferences Updated",
        description: "Your alert preferences have been saved.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Update Preferences",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleToggle = (field: keyof AlertPref, value: boolean) => {
    updatePrefsMutation.mutate({ [field]: value });
  };

  const handleThresholdChange = (value: string) => {
    updatePrefsMutation.mutate({ threshold: value as "info" | "warn" | "fail" });
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <p className="text-muted-foreground">Loading preferences...</p>
        </CardContent>
      </Card>
    );
  }

  const prefs = alertPrefs || {
    emailEnabled: false,
    slackEnabled: false,
    webhookEnabled: false,
    threshold: "warn" as const,
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>Global Alert Preferences</CardTitle>
        <CardDescription>
          Configure how you want to receive notifications about domain issues
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        <div>
          <h3 className="text-sm font-medium mb-4">Notification Channels</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label htmlFor="email-enabled">Email Notifications</Label>
                <p className="text-sm text-muted-foreground">
                  Receive alerts via email
                </p>
              </div>
              <Switch
                id="email-enabled"
                checked={prefs.emailEnabled}
                onCheckedChange={(checked) => handleToggle("emailEnabled", checked)}
                disabled={updatePrefsMutation.isPending}
                data-testid="switch-email-enabled"
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label htmlFor="slack-enabled">Slack Notifications</Label>
                <p className="text-sm text-muted-foreground">
                  Send alerts to configured Slack channels
                </p>
              </div>
              <Switch
                id="slack-enabled"
                checked={prefs.slackEnabled}
                onCheckedChange={(checked) => handleToggle("slackEnabled", checked)}
                disabled={updatePrefsMutation.isPending}
                data-testid="switch-slack-enabled"
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label htmlFor="webhook-enabled">Webhook Notifications</Label>
                <p className="text-sm text-muted-foreground">
                  Send alerts to configured webhook endpoints
                </p>
              </div>
              <Switch
                id="webhook-enabled"
                checked={prefs.webhookEnabled}
                onCheckedChange={(checked) => handleToggle("webhookEnabled", checked)}
                disabled={updatePrefsMutation.isPending}
                data-testid="switch-webhook-enabled"
              />
            </div>
          </div>
        </div>

        <div>
          <h3 className="text-sm font-medium mb-4">Alert Threshold</h3>
          <div className="space-y-2">
            <Label htmlFor="threshold">Minimum Severity Level</Label>
            <Select
              value={prefs.threshold}
              onValueChange={handleThresholdChange}
              disabled={updatePrefsMutation.isPending}
            >
              <SelectTrigger id="threshold" data-testid="select-threshold">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="info">Info - All notifications</SelectItem>
                <SelectItem value="warn">Warning - Only warnings and failures</SelectItem>
                <SelectItem value="fail">Failure - Only critical failures</SelectItem>
              </SelectContent>
            </Select>
            <p className="text-sm text-muted-foreground">
              You'll only receive alerts that meet or exceed this severity level
            </p>
          </div>
        </div>

        <div className="pt-4 border-t">
          <div className="flex items-start gap-3 p-4 bg-muted rounded-lg">
            <Bell className="h-5 w-5 text-muted-foreground mt-0.5" />
            <div className="flex-1">
              <p className="text-sm font-medium">Per-Domain Overrides</p>
              <p className="text-sm text-muted-foreground mt-1">
                You can override these global settings for individual domains from the domain
                detail page. Domain-specific settings will take precedence over these global
                preferences.
              </p>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/pages/settings/destinations-t.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Plus, Webhook, Trash2 } from "lucide-react";
import { SiSlack } from "react-icons/si";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

interface Destination {
  id: string;
  userId: string;
  type: "slack" | "webhook";
  url: string;
  enabled: boolean;
  createdAt: string;
}

export default function DestinationsTab() {
  const { toast } = useToast();
  const [showAddDialog, setShowAddDialog] = useState(false);
  const [destType, setDestType] = useState<"slack" | "webhook">("slack");
  const [destUrl, setDestUrl] = useState("");

  const { data: destinations, isLoading } = useQuery<Destination[]>({
    queryKey: ["/api/destinations"],
  });

  const addDestinationMutation = useMutation({
    mutationFn: async (data: { type: string; url: string; enabled: boolean }) => {
      const res = await apiRequest("POST", "/api/destinations", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/destinations"] });
      setShowAddDialog(false);
      setDestUrl("");
      toast({
        title: "Destination Added",
        description: "Your notification destination has been added successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Add Destination",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const toggleDestinationMutation = useMutation({
    mutationFn: async (data: { id: string; enabled: boolean }) => {
      const res = await apiRequest("PATCH", `/api/destinations/${data.id}`, {
        enabled: data.enabled,
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/destinations"] });
      toast({
        title: "Destination Updated",
        description: "Notification destination has been updated.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Update Destination",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteDestinationMutation = useMutation({
    mutationFn: async (id: string) => {
      const res = await apiRequest("DELETE", `/api/destinations/${id}`);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/destinations"] });
      toast({
        title: "Destination Deleted",
        description: "Notification destination has been removed.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to Delete Destination",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleAddDestination = () => {
    if (!destUrl.trim()) {
      toast({
        title: "URL Required",
        description: "Please enter a webhook URL.",
        variant: "destructive",
      });
      return;
    }

    addDestinationMutation.mutate({
      type: destType,
      url: destUrl.trim(),
      enabled: true,
    });
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <p className="text-muted-foreground">Loading destinations...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle>Notification Destinations</CardTitle>
            <CardDescription>
              Configure where alerts are sent when issues are detected
            </CardDescription>
          </div>
          <Dialog open={showAddDialog} onOpenChange={setShowAddDialog}>
            <DialogTrigger asChild>
              <Button data-testid="button-add-destination">
                <Plus className="h-4 w-4 mr-2" />
                Add Destination
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Add Notification Destination</DialogTitle>
                <DialogDescription>
                  Add a Slack or webhook destination for alert notifications
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4 py-4">
                <div>
                  <Label htmlFor="dest-type">Destination Type</Label>
                  <Select value={destType} onValueChange={(v) => setDestType(v as "slack" | "webhook")}>
                    <SelectTrigger data-testid="select-dest-type">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="slack">Slack Webhook</SelectItem>
                      <SelectItem value="webhook">Generic Webhook</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="dest-url">Webhook URL</Label>
                  <Input
                    id="dest-url"
                    value={destUrl}
                    onChange={(e) => setDestUrl(e.target.value)}
                    placeholder="https://hooks.slack.com/services/..."
                    data-testid="input-dest-url"
                  />
                  <p className="text-xs text-muted-foreground mt-1">
                    {destType === "slack"
                      ? "Get your Slack webhook URL from your workspace settings"
                      : "Enter the URL where notifications should be sent"}
                  </p>
                </div>
              </div>
              <div className="flex justify-end gap-2">
                <Button
                  variant="outline"
                  onClick={() => setShowAddDialog(false)}
                  data-testid="button-cancel-add-dest"
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleAddDestination}
                  disabled={addDestinationMutation.isPending}
                  data-testid="button-confirm-add-dest"
                >
                  {addDestinationMutation.isPending ? "Adding..." : "Add Destination"}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {destinations && destinations.length > 0 ? (
            destinations.map((dest) => (
              <Card key={dest.id} className="p-4" data-testid={`card-dest-${dest.id}`}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    {dest.type === "slack" ? (
                      <SiSlack className="h-5 w-5 text-[#4A154B]" />
                    ) : (
                      <Webhook className="h-5 w-5 text-muted-foreground" />
                    )}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2">
                        <p className="text-sm font-medium capitalize">{dest.type}</p>
                        <Badge variant={dest.enabled ? "default" : "secondary"}>
                          {dest.enabled ? "Enabled" : "Disabled"}
                        </Badge>
                      </div>
                      <p className="text-xs text-muted-foreground truncate max-w-md">
                        {dest.url}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() =>
                        toggleDestinationMutation.mutate({
                          id: dest.id,
                          enabled: !dest.enabled,
                        })
                      }
                      disabled={toggleDestinationMutation.isPending}
                      data-testid={`button-toggle-dest-${dest.id}`}
                    >
                      {dest.enabled ? "Disable" : "Enable"}
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => deleteDestinationMutation.mutate(dest.id)}
                      disabled={deleteDestinationMutation.isPending}
                      data-testid={`button-delete-dest-${dest.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </Card>
            ))
          ) : (
            <div className="text-center py-8">
              <Webhook className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">No destinations configured</p>
              <p className="text-sm text-muted-foreground mb-4">
                Add a destination to receive alert notifications
              </p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/pages/admin/index.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";
import { useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import {
  Loader2,
  AlertCircle,
  Users,
  Globe,
  Activity,
  Bell,
  MailWarning,
  TrendingUp,
} from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from "recharts";

interface AdminMetrics {
  users: {
    byPlan: Record<string, number>;
    total: number;
  };
  domains: {
    total: number;
    monitored: number;
  };
  scans: {
    last24h: number;
    last7d: number;
  };
  alerts: {
    last24h: number;
  };
  email: {
    failuresLast24h: number;
  };
}

interface EventMetrics {
  total: number;
  byType: Record<string, number>;
  timeline: Array<{
    date: string;
    [key: string]: any;
  }>;
}

export default function AdminDashboard() {
  const { isAuthenticated, isLoading: authLoading, user } = useAuth();
  const [, setLocation] = useLocation();

  const { data: planData, isLoading: planLoading } = useQuery<{ plan: string }>({
    queryKey: ["/api/billing/plan"],
    enabled: isAuthenticated,
  });

  // For launch: use Agency plan as admin access proxy
  // TODO: In production, add isAdmin field to user schema
  const isAdmin = planData?.plan === "Agency";

  const { data: metrics, isLoading: metricsLoading, error } = useQuery<AdminMetrics>({
    queryKey: ["/api/admin/metrics"],
    enabled: isAuthenticated && isAdmin,
    retry: 1,
  });

  const { data: events, isLoading: eventsLoading } = useQuery<EventMetrics>({
    queryKey: ["/api/admin/metrics/events"],
    enabled: isAuthenticated && isAdmin,
    retry: 1,
  });

  // Reset metrics query when admin status changes to clear stale errors
  useEffect(() => {
    if (isAdmin) {
      queryClient.resetQueries({ queryKey: ["/api/admin/metrics"] });
      queryClient.resetQueries({ queryKey: ["/api/admin/metrics/events"] });
    }
  }, [isAdmin]);

  if (authLoading || planLoading) {
    return (
      <div className="max-w-7xl mx-auto px-6 py-12 flex items-center justify-center">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  if (!isAuthenticated) {
    return (
      <div className="max-w-7xl mx-auto px-6 py-12">
        <div className="text-center py-12">
          <AlertCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
          <h2 className="text-2xl font-bold mb-2">Authentication Required</h2>
          <p className="text-muted-foreground mb-6">Please sign in to access the admin dashboard</p>
          <Button onClick={() => setLocation("/login")}>Sign In</Button>
        </div>
      </div>
    );
  }

  if (isAuthenticated && planData && !isAdmin) {
    return (
      <div className="max-w-7xl mx-auto px-6 py-12">
        <div className="text-center py-12">
          <AlertCircle className="h-12 w-12 text-destructive mx-auto mb-4" />
          <h2 className="text-2xl font-bold mb-2">Access Denied</h2>
          <p className="text-muted-foreground mb-6">
            You don't have permission to access the admin dashboard
          </p>
          <Button onClick={() => setLocation("/dashboard")}>Go to Dashboard</Button>
        </div>
      </div>
    );
  }

  if (metricsLoading) {
    return (
      <div className="max-w-7xl mx-auto px-6 py-12">
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2">Admin Dashboard</h1>
          <p className="text-muted-foreground">Platform-wide metrics and statistics</p>
        </div>
        <div className="flex items-center justify-center py-12">
          <Loader2 className="h-8 w-8 animate-spin text-primary" />
        </div>
      </div>
    );
  }

  if (error || !metrics) {
    return (
      <div className="max-w-7xl mx-auto px-6 py-12">
        <div className="text-center py-12">
          <AlertCircle className="h-12 w-12 text-destructive mx-auto mb-4" />
          <h2 className="text-2xl font-bold mb-2">Failed to Load Metrics</h2>
          <p className="text-muted-foreground mb-6">
            Unable to fetch admin metrics. Please try again later.
          </p>
          <Button onClick={() => setLocation("/dashboard")}>Go to Dashboard</Button>
        </div>
      </div>
    );
  }

  const activePlanRatio =
    metrics.domains.total > 0
      ? Math.round((metrics.domains.monitored / metrics.domains.total) * 100)
      : 0;

  return (
    <div className="max-w-7xl mx-auto px-6 py-12">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2" data-testid="text-admin-title">
          Admin Dashboard
        </h1>
        <p className="text-muted-foreground">Platform-wide metrics and statistics</p>
      </div>

      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3 mb-8">
        <Card data-testid="card-total-users">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Users</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics.users.total}</div>
            <p className="text-xs text-muted-foreground mt-1">Registered accounts</p>
          </CardContent>
        </Card>

        <Card data-testid="card-total-domains">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Domains</CardTitle>
            <Globe className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics.domains.total}</div>
            <p className="text-xs text-muted-foreground mt-1">
              {metrics.domains.monitored} actively monitored ({activePlanRatio}%)
            </p>
          </CardContent>
        </Card>

        <Card data-testid="card-scans-24h">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Scans (24h)</CardTitle>
            <Activity className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics.scans.last24h}</div>
            <p className="text-xs text-muted-foreground mt-1">
              {metrics.scans.last7d} in last 7 days
            </p>
          </CardContent>
        </Card>

        <Card data-testid="card-alerts-24h">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Alerts (24h)</CardTitle>
            <Bell className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics.alerts.last24h}</div>
            <p className="text-xs text-muted-foreground mt-1">Notifications sent</p>
          </CardContent>
        </Card>

        <Card data-testid="card-email-failures">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Email Failures</CardTitle>
            <MailWarning className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics.email.failuresLast24h}</div>
            <p className="text-xs text-muted-foreground mt-1">Failed deliveries (24h)</p>
          </CardContent>
        </Card>

        <Card data-testid="card-monitoring-rate">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Monitoring Rate</CardTitle>
            <TrendingUp className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{activePlanRatio}%</div>
            <p className="text-xs text-muted-foreground mt-1">Domains with monitoring enabled</p>
          </CardContent>
        </Card>
      </div>

      <div className="grid gap-6 md:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle>Users by Plan</CardTitle>
            <CardDescription>Distribution of users across subscription tiers</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {Object.entries(metrics.users.byPlan)
                .sort((a, b) => b[1] - a[1])
                .map(([plan, count]) => (
                  <div
                    key={plan}
                    className="flex items-center justify-between"
                    data-testid={`row-plan-${plan.toLowerCase()}`}
                  >
                    <div className="flex items-center gap-3">
                      <Badge
                        variant={
                          plan === "Agency" ? "default" : plan === "Pro" ? "secondary" : "outline"
                        }
                      >
                        {plan}
                      </Badge>
                    </div>
                    <div className="flex items-center gap-4">
                      <span className="text-sm text-muted-foreground">
                        {Math.round((count / metrics.users.total) * 100)}%
                      </span>
                      <span className="text-lg font-semibold">{count}</span>
                    </div>
                  </div>
                ))}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Activity Summary</CardTitle>
            <CardDescription>Recent platform activity highlights</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Average scans per day</span>
                <span className="text-lg font-semibold">
                  {Math.round(metrics.scans.last7d / 7)}
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Alerts per scan (24h)</span>
                <span className="text-lg font-semibold">
                  {metrics.scans.last24h > 0
                    ? (metrics.alerts.last24h / metrics.scans.last24h).toFixed(2)
                    : "0"}
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Email delivery rate</span>
                <span className="text-lg font-semibold">
                  {metrics.alerts.last24h > 0
                    ? (
                        ((metrics.alerts.last24h - metrics.email.failuresLast24h) /
                          metrics.alerts.last24h) *
                        100
                      ).toFixed(1)
                    : "100"}
                  %
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Domains per user</span>
                <span className="text-lg font-semibold">
                  {metrics.users.total > 0
                    ? (metrics.domains.total / metrics.users.total).toFixed(1)
                    : "0"}
                </span>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Events Analytics Section */}
      <div className="mt-8">
        <h2 className="text-2xl font-bold mb-4">Product Analytics</h2>

        {eventsLoading && (
          <div className="flex items-center justify-center py-12">
            <Loader2 className="h-8 w-8 animate-spin text-primary" />
          </div>
        )}

        {!eventsLoading && !events && (
          <Card>
            <CardContent className="pt-6">
              <div className="text-center py-8">
                <AlertCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-muted-foreground">Failed to load events data</p>
              </div>
            </CardContent>
          </Card>
        )}

        {!eventsLoading && events && (
          <div className="space-y-6">
            <div className="grid gap-6 md:grid-cols-2">
              <Card>
                <CardHeader>
                  <CardTitle>Event Activity (Last 30 Days)</CardTitle>
                  <CardDescription>Total tracked events: {events.total}</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {Object.entries(events.byType)
                      .sort((a, b) => b[1] - a[1])
                      .map(([eventType, count]) => (
                        <div
                          key={eventType}
                          className="flex items-center justify-between"
                          data-testid={`row-event-${eventType.toLowerCase()}`}
                        >
                          <span className="text-sm font-medium">{eventType.replace(/_/g, ' ')}</span>
                          <Badge variant="secondary">{count}</Badge>
                        </div>
                      ))}
                    {Object.keys(events.byType).length === 0 && (
                      <p className="text-sm text-muted-foreground text-center py-4">
                        No events tracked yet
                      </p>
                    )}
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Event Distribution</CardTitle>
                  <CardDescription>Breakdown by event type</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {events.total === 0 ? (
                      <p className="text-sm text-muted-foreground text-center py-4">
                        No events to display
                      </p>
                    ) : (
                      Object.entries(events.byType)
                        .sort((a, b) => b[1] - a[1])
                        .map(([eventType, count]) => {
                          const percentage = events.total > 0 
                            ? Math.round((count / events.total) * 100) 
                            : 0;
                          return (
                            <div key={eventType} className="space-y-1">
                              <div className="flex items-center justify-between text-sm">
                                <span className="text-muted-foreground">{eventType.replace(/_/g, ' ')}</span>
                                <span className="font-medium">{percentage}%</span>
                              </div>
                              <div className="h-2 bg-muted rounded-full overflow-hidden">
                                <div
                                  className="h-full bg-primary transition-all"
                                  style={{ width: `${percentage}%` }}
                                />
                              </div>
                            </div>
                          );
                        })
                    )}
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Timeline Chart */}
            {events.timeline.length > 0 && (
              <Card>
                <CardHeader>
                  <CardTitle>Events Timeline</CardTitle>
                  <CardDescription>Daily event activity over the last 30 days</CardDescription>
                </CardHeader>
                <CardContent>
                  <ResponsiveContainer width="100%" height={300}>
                    <LineChart data={events.timeline}>
                      <CartesianGrid strokeDasharray="3 3" className="stroke-muted" />
                      <XAxis 
                        dataKey="date" 
                        className="text-xs"
                        tickFormatter={(value) => new Date(value).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                      />
                      <YAxis className="text-xs" />
                      <Tooltip 
                        labelFormatter={(label) => new Date(label).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}
                      />
                      <Legend />
                      {Object.keys(events.byType).map((eventType, index) => {
                        const colors = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6', '#ec4899'];
                        return (
                          <Line
                            key={eventType}
                            type="monotone"
                            dataKey={eventType}
                            name={eventType.replace(/_/g, ' ')}
                            stroke={colors[index % colors.length]}
                            strokeWidth={2}
                            dot={{ r: 3 }}
                            activeDot={{ r: 5 }}
                          />
                        );
                      })}
                    </LineChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/forbidden.tsx.content:
